// GLOBAL
FunctionHandler.register( ['*'], function(){

  // Detect OS & add a class to the HTML element, enabling PC ProximaNova-Light fix
  $('html').addClass( navigator.platform ).removeClass('no-js');

  // links - on state
  $('a').each(function(){if ($(this).attr('href') == (window.location.href) || $(this).attr('href') == (window.location.pathname)){$(this).addClass('on');}});

  // bind change event to select
  $('.dynamic_select').on('change',function (){var url = $(this).val(); if (url) {window.location = url;}return false;});

  // Lazyload images
  $('img.lazy').show().lazyload({effect:'fadeIn', threshold:20});

  // Show slideshow nav controls on hover
  $('#features li img').hoverIntent(function () {
    $('p.ss-icon').addClass('ss-icon-show');},function () {$('p.ss-icon').removeClass('ss-icon-show');});
  
  // If no image zooming has been specified, remove the anchors from around the right column images (not the most efficient solution; ideally, we could tell Drupal not to use the colorbox style here)
  // FIX!! THIS SHOULD BE DONE USING theme_image_style instead
  if ($('.field-name-field-rightimage.no-zoom a.colorbox').length) {
    $('.field-name-field-rightimage.no-zoom a.colorbox img').unwrap();
  }
  
  // Slideshow Module
  if ($('#slideshow').length) {
    // Force it to wait until all images are loaded before applying cycle, otherwise image height is messed up
    $(window).load(function() {
      $('#slideshowwrap').show(); // This is hidden so that all the images aren't visible before they all disappear
      $('.field-name-field-rightimage.slideshow .slide img').each(function() {
        // Set width of captions when image is smaller than 300px
        var imgWidth = $(this).width();
        if (imgWidth < 300) {
          $(this).siblings('p').css('width', imgWidth + 'px');
        }
      });
      $('#slideshow').cycle({fx:'fade',speed:300,timeout:0,next:'.next',prev:'.prev',cleartypeNoBg:true});
    });
  }

  // Set width of captions when image is smaller than 300px but at least 218px
  $(window).load(function() {
    $('.field-name-field-rightimage.no-slideshow .slide img').each(function() {
      var imgWidth = $(this).width();
      if (imgWidth < 300 && imgWidth >= 218) {
        $(this).siblings('p').css('width', imgWidth + 'px');
      }
    });
  });  

  // Image galleries
  // Cannot use the function handler since we do not have IDs on the body tag. Room for improvement
  if ($('.node-type-image-gallery').length) {
    var galleryOptions = {
      fallback: {
        theme: "fade",
        speed: 500
      },      
      nextButton: true,
      prevButton: true,
      preloader: true,
      hideFramesUntilPreloaded: false,
      pauseOnHover: true,
      transitionThreshold: 250,
      reverseAnimationsWhenNavigatingBackwards: false,
      autoPlayDelay: 6000,
      keyNavigation: true,
      swipeNavigation: true,
      swipeEvents: {left: 'next', right: 'prev', up: false, down: false},
    };
    var gallerySequence = $('body.not-front #features').sequence(galleryOptions).data('sequence');

    gallerySequence.afterLoaded = function() {
      $('#featurenav').fadeIn(100);
      $('#featurenav li:nth-child('+(gallerySequence.settings.startingFrameID)+') button').addClass('active');
      $('#mainfeatures').addClass('gradient');
    };

    gallerySequence.beforeNextFrameAnimatesIn = function() {
      $('#featurenav li:not(:nth-child('+(gallerySequence.nextFrameID)+')) button').removeClass('active');
      $('#featurenav li:nth-child('+(gallerySequence.nextFrameID)+') button').addClass('active');
    };

    $('#featurenav li').bind('click focusin',function(){
      if(!gallerySequence.active){
        $(this).children('button').removeClass('active').children('button').addClass('active');
        gallerySequence.nextFrameID = $(this).index()+1;
        gallerySequence.goTo(gallerySequence.nextFrameID);
      }
    });
  }

  // A hack to add the appropriate classes to social media links in the footer. Once the old theme is retired, the view can be updated and this hack removed.
  $('#block-views-footer-socialmedia .view-content a').each(function() {
    var socialClass = 'ss-social ss-' + $(this).attr('class');
    $(this).addClass(socialClass).empty();
  });

  // Toggle the features upon clicking the subnav toggle
  $('.btn-subnavbar').bind('click', function() {
    $('#features-outer').slideToggle();
  });

});

